import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { Pixelify } from "react-pixelify";

const src = require("./image.png");

class PixelateSlider extends React.Component{
  render() {
    return (
      <div className="my-5">
        <label htmlFor="myRange1">Pixelation: {this.props.value}</label>
        <input
          type="range" min="0" max="30" class="slider" id="myRange1"
          onChange={this.props.onChange}
        />
      </div>
    );
  }
}

class MyImg extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      pixelValue: 0,
    }
  }

  handleChange = (i) => {
    this.setState({pixelValue: i.target.value});
  }

  renderSlider(){
    return (
      <PixelateSlider
        value={this.state.pixelValue}
        onChange={this.handleChange}
      />
    );
  }

  render() {
    return (
      <div>
        <div>
          <Pixelify
          src={src}
          centered={true}
          pixelSize={this.state.pixelValue}
          />
        </div>
        <div>
          {this.renderSlider()}
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  render() {
    return (
      <div className="app">
        <div className="app-img">
          <MyImg />
        </div>
        <div className="img-fields">
          <div></div>
        </div>
      </div>
    );
  }
}


// ======================================
ReactDOM.render(
  <App />,
  document.getElementById('root')
);
