import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { Pixelify } from "react-pixelify";

const src = require("./image.png");

class SliderPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      pixelValue: 0,
    };
  }

  render() {
    return (
      <div className="my-5">
        <label htmlFor="myRange1">Pixelation: {this.props.value}</label>
        <input
          type="range" min="0" class="slider" id="myRange1"
          onChange={this.handleChange}
        />
      </div>
    );
  }
}


class MyImg extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      pixelValue: 0,
    }
  }

  renderSlider(){
    return (
      <SliderPage
        value={this.state.pixelValue}
        onChange={this.handleChange}
      />
    );
  }

  render() {
    return (
      <div>
        <div>
          <Pixelify
          src={src}
          width={250}
          height={250}
          centered={true}
          pixelSize={this.state.pixelValue}
          />
        </div>
        <div>
          {this.renderSlider()}
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  render() {
    return (
      <div className="app">
        <div className="app-img">
          <MyImg />
        </div>
        <div className="img-fields">
          <div></div>
        </div>
      </div>
    );
  }
}


// ======================================
ReactDOM.render(
  <App />,
  document.getElementById('root')
);
